css:
@page { 
    size: A4 landscape;
    margin: 12mm;
}

body, .print-container {
    font-family: "Times New Roman", serif;
    font-size: 14px;
    color: #000;
    -webkit-print-color-adjust: exact !important;
    print-color-adjust: exact !important;
}

.print-container {
    width: 100%;
    box-sizing: border-box;
}

.header {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 15px;
    margin-bottom: 10px;
}

.header-left img { width: 110px; }
.header-right { text-align: center; }

.company-title { font-size: 18px; font-weight: bold; }
.company-subtitle { font-size: 15px; font-weight: bold; }
.company-info { font-size: 13px; }

.form-title {
    text-align: center;
    font-size: 20px;
    font-weight: bold;
    margin-top: 5px;
}
.form-date { text-align: center; margin-bottom: 10px; }

.sub-info-line {
    display: flex;
    justify-content: space-between;
}

.doc-number { text-align: right; margin-top: 8px; }
.info-block { margin: 12px 0; line-height: 1.6; }

.table-wrapper {
    width: 100%;
    overflow-x: auto;
}

.material-table {
    width: 100%;
    border-collapse: collapse;
    table-layout: auto;
}

.material-table th,
.material-table td {
    border: 1px solid black;
    padding: 4px 6px;
    text-align: center !important;
    vertical-align: middle !important;
    white-space: nowrap;
    font-size: 13px;
}

.material-table th {
    background: #69a6f4 !important;
    color: #333;
}

@media print {
    .header,
    .info-block,
    .notes,
    .signature-section {
        page-break-inside: avoid !important;
    }

    .material-table {
        width: auto !important;
        max-width: 100% !important;
        table-layout: auto !important;
    }

    .material-table th,
    .material-table td {
        white-space: normal !important;
        word-break: break-word !important;
        overflow: visible !important;
        text-overflow: initial !important;
        line-height: 1.2 !important;
    }

    .material-table thead {
        display: table-header-group !important;
    }
}

.notes { margin-top: 15px; }
.notes .bold { font-weight: bold; }

.notes-grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    column-gap: 40px;
    row-gap: 6px;
    margin-top: 6px;
}

.signature-section {
    display: flex;
    justify-content: space-between;
    margin-top: 25px;
    text-align: center;
    font-weight: bold;
}

@media screen and (max-width: 768px) {

    .header { flex-direction: column; }

    .signature-section {
        flex-direction: column;
        gap: 15px;
    }

    .signature-section > div { width: 100%; }

    .print-container {
        min-height: calc(100vh + 200px);
        padding-bottom: 200px;
    }

    div.print-preview-wrapper {
        height: auto !important;
        overflow: auto !important;
    }
}
