css:
@page { 
    size: A4 landscape;
    margin: 12mm;
}

body, .print-container {
    font-family: "Times New Roman", serif;
    font-size: 14px;
    color: #000;
    -webkit-print-color-adjust: exact !important;
    print-color-adjust: exact !important;
}

.print-container { width: 100%; box-sizing: border-box; }

/* Header */
.header { display: flex; justify-content: center; align-items: center; gap: 15px; margin-bottom: 10px; }
.header-left img { width: 110px; }
.header-right { text-align: center; }

.company-title { font-size: 18px; font-weight: bold; }
.company-subtitle { font-size: 15px; font-weight: bold; }
.company-info { font-size: 13px; }

.form-title { text-align: center; font-size: 20px; font-weight: bold; margin-top: 5px; }
.form-date { text-align: center; margin-bottom: 10px; }

.sub-info-line { display: flex; justify-content: space-between; }
.doc-number { text-align: right; margin-top: 8px; }

.info-block { margin: 12px 0; line-height: 1.6; }

/* Table */
.table-wrapper { width: 100%; overflow-x: auto; }

.material-table { width: 100%; border-collapse: collapse; table-layout: auto; }
.material-table th, .material-table td {
    border: 1px solid black;
    padding: 4px 6px;
    text-align: center !important;
    vertical-align: middle !important;
    white-space: nowrap;
    font-size: 13px;
}
.material-table th {
    background: #69a6f4 !important;
    color: #333;
    font-weight: bold;
    font-size: 14px !important ;
}

/* Print Fix */
@media print {
    .header, .info-block, .notes, .signature-section { page-break-inside: avoid !important; }

    .material-table tbody tr:nth-child(even) {
        background-color: #e6e6e6 !important;
        -webkit-print-color-adjust: exact !important;
        print-color-adjust: exact !important;
    }

    /*Tắt header khi sang trang*/
     .material-table thead {
        display: table-row-group !important;
    }

    /*Bật header khi sang trang*/
    /*.material-table thead { display: table-header-group !important; }*/

    .material-table th, .material-table td {
        white-space: normal !important;
        word-break: break-word !important;
    }
}

/* Notes */
.notes { margin-top: 15px; }
.notes .bold { font-weight: bold; }

.notes-grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    column-gap: 40px;
    row-gap: 6px;
}

/* Signature */
.signature-section {
    display: flex;
    justify-content: space-between;
    margin-top: 25px;
    font-weight: bold;
    text-align: center;
}

/* Mobile Fix */
@media screen and (max-width: 768px) {
    .header { flex-direction: column; }

    .signature-section { flex-direction: column; gap: 15px; }
    .signature-section > div { width: 100%; }

    .print-container {
        min-height: calc(100vh + 200px);
        padding-bottom: 200px;
    }

    div.print-preview-wrapper {
        height: auto !important;
        overflow: auto !important;
    }
}

.material-table tbody tr:nth-child(odd) {
    background: #ffffff;
}

.material-table tbody tr:nth-child(even) {
    background: #f3f3f3;
}
