css:
@page {
  size: A4;
  margin: 15mm;
}
.table-scroll {
  width: 100%;
  max-height: 450px;
  overflow-x: auto; 
  overflow-y: auto;
  display: block;
}

.print-container {
  font-family: "Times New Roman", serif;
  font-size: 14px;
  color: #000;
}

.bold { font-weight: bold; }
.italic { font-style: italic; }
.uc { text-transform: uppercase; }
.mt_25 {
    margin-top: 25px;
}

.top-header {
  display: flex;
  justify-content: space-between;
  margin-bottom: 15px;
}

.top-header .right {
  text-align: center;
}

.underline {
  text-decoration: underline;
}

.title {
  text-align: center;
  font-size: 18px;
  margin-top: 10px;
}

.subtitle {
  text-align: center;
  margin-bottom: 15px;
}

.info-block {
  margin-bottom: 8px;
  line-height: 1.5;
}

.section-title {
  font-weight: bold;
  margin: 10px 0 5px;
}

.material-table {
  width: 100%;
  border-collapse: collapse;
}

.material-table th,
.material-table td {
  border: 1px solid #000;
  text-align: center !important;
  vertical-align: middle !important;
}

.material-table th {
  text-align: center;
  font-weight: bold;
}

.print-format th {
    color: #333 !important;
    background: #69a6f4 !important;
}

.text-right { text-align: right; }

.summary td {
  font-weight: bold;
}

.sign-block {
  display: flex;
  justify-content: space-between;
  margin-top: 40px;
  text-align: center;
}

.info-row div {
    all: unset;
    display: block;
    line-height: 1.5;
}

.info-row {
    display: grid;
    grid-template-columns: 90px 220px 70px 200px;
    align-items: baseline;
    margin-bottom: 4px;
    column-gap: 6px;
}

.info-label {
    white-space: nowrap;
    font-weight: normal;
}

.info-value {
    padding-left: 2px;
}

.info-value.bold {
    font-weight: bold;
}

.date-right {
  text-align: right;
  margin-top: 20px;
}

@media screen and (max-width: 768px) {

  .sign-block {
    gap: 25px;
    text-align: center;
  }

  .sign-block .bold {
    font-size: 13px !important;
  }

  .sign-block .mt_25 {
    font-size: 12px !important;
  }

}
